ControlPropertyState.AutoRuleBindingString:
With({
  background: sigd_bonin_sidebar.EsquemaDeCores.AzulEscuroBonin.Texto,
  altura: Parent.Height - 11,
  raio: sigd_bonin_sidebar.RaioDaBorda,
  espacamento: sigd_bonin_sidebar.Espaçamento,
  margem: sigd_bonin_sidebar.RaioDaBorda,
  sombra: sigd_bonin_sidebar.EsquemaDeCores.SombraEscura.Texto
},
"
<div style='
    background:" & background & ";
    height:" & altura & "px;
    border-radius: " & raio & ";
    margin: " & espacamento & "px " & espacamento & "px " & espacamento & "px " & espacamento & "px;
    box-shadow: 2px 2px 2px 0px " & sombra & "'
></div>
"
)

Rules.InvariantScript:
With(
  {
    background: sigd_bonin_cabecalho.EsquemaDeCores.AzulEscuroBonin.Texto,
    altura: Parent.Height - sigd_bonin_cabecalho.Medidas.Espacamento*3,
    raio: sigd_bonin_cabecalho.RaioDaBorda,
    espacamento: sigd_bonin_cabecalho.Medidas.Espacamento,
    margem: sigd_bonin_cabecalho.RaioDaBorda,
    sombra: sigd_bonin_cabecalho.EsquemaDeCores.SombraEscura.Texto,
    titulo: Upper(
      If(
        !IsBlank(
          LookUp(
            sigd_bonin_cabecalho.ListaDasTelas,
            Tela.Name = App.ActiveScreen.Name,
            NomeTela
          )
        ),
        LookUp(
          sigd_bonin_cabecalho.ListaDasTelas,
          Tela.Name = App.ActiveScreen.Name,
          NomeTela
        ),
        LookUp(
          sigd_bonin_cabecalho.ListaDasTelas,
          !IsBlank(
            LookUp(SubMenu, Tela.Name = App.ActiveScreen.Name)
          ),
          LookUp(SubMenu, Tela.Name = App.ActiveScreen.Name, NomeTela)
        )
      )
    )
  },
  "
  <div style='
    background: " & background & ";
    height: " & altura & "px;
    border-radius: " & raio & ";
    margin: " & espacamento & "px " & espacamento & "px " & espacamento*2 & "px 0px;
    box-shadow: 2px 2px 2px 0px " & sombra & ";
    display: flex;
    justify-content: center;
    align-items: center;
    font-family: " & App.Theme.Font & ";
    font-weight: bold;
    font-size: 24px;
    color: white;
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.8);
  '>
    " & titulo & "
  </div>"
)
